package dev.lapisteam.webapp.discord.models;import com.google.gson.JsonArray;import com.google.gson.JsonObject;import java.util.ArrayList;import java.util.List;public class Component {    private String userName;    private String avatar;    private String content;    private List<EmbedMessage> embedMessageList = new ArrayList<>();    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    public List<EmbedMessage> getEmbedMessageList() {        return embedMessageList;    }    public void setEmbedMessageList(List<EmbedMessage> embedMessageList) {        this.embedMessageList = embedMessageList;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getAvatar() {        return avatar;    }    public void setAvatar(String avatar) {        this.avatar = avatar;    }    public JsonObject toJsonObject(){        JsonObject jsonObject = new JsonObject();        jsonObject.addProperty("content", content);        if(userName != null) jsonObject.addProperty("username", userName);        if(avatar != null) jsonObject.addProperty("avatar_url", avatar);        if(!embedMessageList.isEmpty()) {            JsonArray jsonElements = new JsonArray();            for (EmbedMessage embedMessage : embedMessageList) {                JsonObject embed = new JsonObject();                embed.addProperty("title", embedMessage.getTitle());                embed.addProperty("description", embedMessage.getDescription());                if(embedMessage.getColor() != null) embed.addProperty("color", Integer.parseInt(embedMessage.getColor(), 16));                if(embedMessage.getFields() != null && !embedMessage.getFields().isEmpty()) {                    JsonArray fields = new JsonArray();                    embedMessage.getFields().forEach((name, body) -> {                        JsonObject field = new JsonObject();                        field.addProperty("name", name);                        field.addProperty("value", body);                        fields.add(field);                    });                    embed.add("fields", fields);                }                if(embedMessage.getFooter() != null) {                    JsonObject footer = new JsonObject();                    footer.addProperty("text", embedMessage.getFooter());                    embed.add("footer", footer);                }                jsonElements.add(embed);            }            jsonObject.add("embeds", jsonElements);        }        return jsonObject;    }}